package lec1;

public class arithmetic {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a=9;
		int b=14;
		int c=a+b;
		System.out.println(c);
		
		c=a-b;
		System.out.println(c);
		
		System.out.println(a++);//post increment =>first prints then increases its value
		System.out.println(a);
		
		System.out.println(b--);//post decrement =>first prints then decreases its value then 
		System.out.println(b);
		
		System.out.println(7/2.0);
		
		

	}

}
