package String;

public class string_add {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		String s1 = "hello";
		String s2 = "bye";

		String s= s1+s2;
		String s3=s1.concat(s2);//used to concat two strings
		System.out.println(s);
		System.out.println(s3);
	}

}
